INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'Cuando el Mainframe era la opción más adecuada, la mayoría de empresas podían permitirse esta solución', 'Falso');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'La llegada de los ordenadores personales hizo que muchas empresas usaran esta nueva solución', 'Verdadero');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'Los sistemas operativos iniciales estaban orientados al mercado doméstico, más que al profesional', 'Verdadero');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'Los servicios en la nube permiten reducir los gastos en mantenimiento y seguridad', 'Verdadero');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'El servicio en la nube puede resultar en algunos contextos más caro que una infraestructura propia', 'Verdadero');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'Los servicios en la nube repercuten en quien lo contrata dependiendo del gasto eléctrico usado', 'Falso');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'En la actualidad, muchas empresas usan ambas opciones (servidores propios, servicio en nube, o combinación de ambos)', 'Verdadero');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'Guardar determinados datos en determinados servicios en la nube puede acarrear un incumplimiento de la ley', 'Verdadero');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'De necesitar más potencia, no podremos contratar más de la que contratamos al principio de solicitar el servicio en la nube', 'Falso');
INSERT INTO preguntas (tipo, texto, respuesta) VALUES ('VF', 'Si optamos por tener servidor propio, deberemos invertir inicialmente en hardware', 'Verdadero');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', '¿Cuál era la opción más viable en los primeros años de la informática?', 'Mainframes', 'Mainframes|Servicios en la nube|Ordenadores personales|Apuntarlo en papel');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', '¿Cuál es una de las mayores ventajas si tenemos servidor propio?', 'Evitar un posible espionaje', 'Evitar un posible espionaje|Reducción de costos|Uso de servicios en la nube|El servidor propio es gratis');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', '¿Qué propició el lanzamiento de los servicios en la nube?', 'Internet', 'Internet|Mainframes de precio muy elevado|Mejores componentes de Hardware|Excesivo coste en mantenimiento del servidor propio');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', '¿Qué hay que tener en cuenta a la hora de elegir si nube o servidor propio?', 'Cumplimiento de las leyes de protección de datos', 'Cantidad de terminales|Instalación de mainframes|Cumplimiento de las leyes de protección de datos|Si tenemos espacio en la oficina para un servidor propio o no');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', '¿A qué se ha tendido en los últimos años en cuanto a la instalación de sistemas ERP?', 'Multiplicación de posibilidades para la instalación', 'Multiplicación de posibilidades para la instalación|Siguen dominando los mainframes|Uso exclusivo de ordenadores personales|Aumento en la potencia');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', '¿Qué es el escalado horizontal en el contexto del hardware de un servidor?', 'Aumentar las prestaciones del servidor', 'Aumentar las prestaciones del servidor|Aumentar la potencia|Mayor inversión en equipos|Escalado vertical');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', 'Si contratamos servicios en la nube no tendremos gastos relacionados con:', 'Hardware del servidor', 'Hardware del servidor|Nóminas|Costes de producción|Compra de licencias');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', 'Una de las desventajas de tener un servidor en la propia empresa es:', 'Dependencia de una buena conexión a internet', 'Dependencia de una buena conexión a internet|Reducción de costos|Evitar riesgos como el espionaje industrial|Uso de servicios en la nube');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', 'Una de las desventajas de tener servidor en la nube es:', 'Mayor precio en la infraestructura', 'Almacenamiento físico de datos en la empresa|Bajo consumo eléctrico|Mayor precio en la infraestructura|Independencia de la conexión a internet');
INSERT INTO preguntas (tipo, texto, respuesta, opciones) VALUES ('SC', '¿Qué tipo de ERP se usará durante el curso?', 'Odoo Community Edition', 'Odoo Community Edition|Enterprise Edition|Software de pago|Mezcla de pago y gratuito');